import React, { useEffect, useState } from 'react';

const ProductList = () => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        getProducts();
    }, []);

    const getProducts = async () => {
        try {
            const response = await fetch('http://localhost:3001/products');
            const result = await response.json();
            setProducts(result);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    }

    const deleteProduct = async (id) => {
        try {
            console.warn(id);
            const response = await fetch(`http://localhost:3001/product/${id}`, {
                method: "DELETE"
            });
            const result = await response.json();
            if (result) {
                alert("Product deleted");
                // Optionally, update the state to reflect the deleted product
                setProducts(prevProducts => prevProducts.filter(product => product._id !== id));
            }
        } catch (error) {
            console.error('Error deleting product:', error);
        }
    }

    return (
        <div className="product-list">
            <h1>Products</h1>

            <ul>
                <li className="bold-text">Pno</li>
                <li className="bold-text">Product Name</li>
                <li className="bold-text">Price</li>
                <li className="bold-text">Category</li>
                <li className="bold-text">Operation</li>
            </ul>

            {products.map((item, index) => (
                <ul key={item._id}>
                    <li>{index + 1}</li>
                    <li>{item.name}</li>
                    <li>{item.price}</li>
                    <li>{item.category}</li>
                    <li><button onClick={() => deleteProduct(item._id)}>Delete</button></li>
                </ul>
            ))}
        </div>
    );
}

export default ProductList;
