import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const UpdateProduct = () => {
    const [name, setProductName] = useState("");
    const [price, setPrice] = useState("");
    const [category, setCategory] = useState("");
    const [company, setCompany] = useState("");


    const { id } = useParams();

    useEffect(() => {
        // Fetch product details from the backend based on the ID
        const fetchProductDetails = async () => {
            try {
                const response = await fetch(`/product/${id}`);
                const data = await response.json();
                if (data.result !== "No record found") {
                    setProductName(data.name);
                    setPrice(data.price);
                    setCategory(data.category);
                    setCompany(data.company);
                } else {
                    console.error("No record found");
                }
            } catch (error) {
                console.error("Error fetching product details:", error);
            }
        };

        fetchProductDetails();
    }, [id]);


    const updateProductData = async () => {
        // Implement your update logic here
        console.warn(name, price, category, company);
    };

    return (
        <div className="addproduct">
            <h1>Update product</h1>
            <input className="inputBox" type="text" placeholder={name} value={name} onChange={(e) => setProductName(e.target.value)} />
            <input className="inputBox" type="text" placeholder={price} value={price} onChange={(e) => setPrice(e.target.value)} />
            <input className="inputBox" type="text" placeholder="Enter category" value={category} onChange={(e) => setCategory(e.target.value)} />
            <input className="inputBox" type="text" placeholder="Enter company" value={company} onChange={(e) => setCompany(e.target.value)} />
            <button onClick={updateProductData} className="appButton" type="button">Update</button>
        </div>
    );
};

export default UpdateProduct;
