import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const UpdateProduct = () => {
    const [name, setProductName] = useState("");
    const [price, setPrice] = useState("");
    const [category, setCategory] = useState("");
    const [company, setCompany] = useState("");

    const { id } = useParams();

    useEffect(() => {
        const fetchProductDetails = async () => {
            let result = await fetch('http://localhost:3001/product/${id}');
            result = await result.json();
            if (data.result !== "No record found") {
                // Set the initial state based on fetched product details
                setProductName(result.name);
                setPrice(result.price);
                setCategory(result.category);
                setCompany(result.company);
            } else {
                console.error("No record found");
            }
        };

        fetchProductDetails();
    }, [id]);

    const updateProductData = async () => {
        // Implement your update logic here
        console.warn(name, price, category, company);
    };

    return (
        <div className="addproduct">
            <h1>Update product</h1>
            <input className="inputBox" type="text" placeholder={name}  value={name} onChange={(e) => setProductName(e.target.value)} />
            <input className="inputBox" type="text" placeholder={price}  value={price} onChange={(e) => setPrice(e.target.value)} />
            <input className="inputBox" type="text" placeholder={category} value={category} onChange={(e) => setCategory(e.target.value)} />
            <input className="inputBox" type="text" placeholder={company}  value={company} onChange={(e) => setCompany(e.target.value)} />
            <button onClick={updateProductData} className="appButton" type="button">Update</button>
        </div>
    );
};

export default UpdateProduct;
